import { useEffect, useState } from 'react';
import { BrowserRouter, Route, Routes } from 'react-router-dom';
import ShowTodo  from './components/showTodo';
import CreateTodo from './components/createTodo';
import axios from 'axios';
import './App.css';



function App() {
  const [todo, setTodo] = useState([]);
  const [open, setOpen] = useState(false);
  const [id, setId] = useState("");
  const [update, setUpdate] = useState(false);
  const [data, setData] = useState({ title: "", description: "" });



  useEffect({
    updateTodos,
  },[])

  function updateTodos() {
    axios
        .get("http://localhost:8000/todo")
        .then((res) => {
            console.log(res.data);
            setTodo(res.data);
        })
        .catch((err) => {
            console.log(err.message);
        });
  }

  function handleChange(e) {
    setData((data) => ({ ...data, [e.target.name]: e.target.value }));
  }

  function handleEdit(e) {
    setId(e.target.name);
    setOpen(true);
}

  function handleUpdate() {
      console.log("update:", update, !update);
      setUpdate(!update);
  }

  function handleDelete(e) {
      axios.delete(`http://localhost:8000/${e.target.name}`);

      setTodo((data) => {
          return data.filter((todo) => todo._id !== e.target.name);
      });
  }

  function handleClose() {
      setId("");
      setOpen(false);
  }

  const handleSubmit = (e) => {
       e.preventDefault();

           axios
          .post("http://localhost:8000/post/todo", data)
          .then((res) => {
              // setData({ description: e.target.value });
              console.log(res.data.message);
          })
          .catch((err) => {
              console.log("Error couldn't create TODO");
              console.log(err.message);
          });

          // console.log(data)
  }
  return (
    <div className="App">
      <header>
        <h1 className="center">Todo Tracker</h1>
      </header>
      <body>
      <form  class="add-form" >
        <div class="form-control">
          <label for="text">Task:</label>
          <input
            type="text"
            name="title"
            value={data.title}
            onChange={handleChange}
            id="text"
            placeholder="Add Task"
          />
          <input
            type="text"
            name="description"
            value={data.description}
            onChange={handleChange}
            id="text"
            placeholder="Add Task"
          />
        </div>
        <input type="submit" class="green" value="Submit" onClick={handleSubmit} class="btn btn-block" />
      </form> 


      </body>
    </div>
  );
}

export default App;
