import { useEffect, useState } from 'react';
import { BrowserRouter, Route, Routes } from 'react-router-dom';

import axios from 'axios';
import './App.css';


function App() {

  const [data, setData] = useState({ title: "", description: "" });

  useEffect(
    function () {
        axios
            .get("http://localhost:8000/todo")
            .then((res) => {
                // console.log(res.data);
                setData(res.data);
            })
            .catch((err) => {
                console.log(err.message);
            });
    });


    function handleChange(e) {
    //  setItem({ title: e.target.value})
      setData((data) => ({ ...data, [e.target.name]: e.target.value} ));

  }

  function handleSubmit(e) {
      e.preventDefault();

      axios
          .post("http://localhost:8000/post/todo", data)
          .then((res) => {
              // setData({ title: "", description: "" });
              console.log(res.data.message);
          })
          .catch((err) => {
              console.log("Error couldn't create TODO");
              console.log(err.message);
          });

  }

  return (
    <div className="App">
      <header>
        <h1 className="center">Todo Tracker</h1>
      </header>
      <body>
      <form
        onSubmit={handleSubmit}
        className="form-container"
        noValidate
      >
        <label className="label" htmlFor="title">
            Title
        </label>
        <input
           type="text"
           name="title"
           value={data.title}
           onChange={handleChange}
           className="input"
id="text"
placeholder="Add Task"
        />
        <label className="label" htmlFor="description">
            Description
        </label>
        <input
            type="text"
            name="description"
            value={data.description}
            onChange={handleChange}
            className="input"
        />
        <button type="submit" className="button">
            create todo
        </button>
    </form>




      <form  class="add-form" onSubmit={handleSubmit} >
        <div class="form-control">
          <label for="text">Task:</label>
          <input
             type="text"
                        name="title"
                        value={data.title}
                        onChange={handleChange}
                        className="input"
            id="text"
            placeholder="Add Task"
          />
          {/* <input
            type="text"
            name="description"
            value={data.description}
            onChange={handleChange}
            id="text"
            placeholder="Add Task"
          /> */}
        </div>
        <input type="submit" class="green" value="submit" class="btn btn-block" />

      </form> 
      <section className="container">
           <ul>
              {Object.keys(data).map((item, i) => {
              return <li>{data[item].title}</li>;
            })
          }
           </ul>
        </section>

      </body>
    </div>
  );
}

export default App;
