import { useEffect, useState } from 'react';
import { BrowserRouter, Route, Routes } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';
import ScrollArea from "react-scrollbar";
import './App.css';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome"
import { solid, regular, brands } from '@fortawesome/fontawesome-svg-core'

function App() {

  const [data, setData] = useState({ title: "", description: "" });
  const [item, setItem] = useState({ title: "", description: "" });

  useEffect(() => {
    run()
  })

function run() {
  axios
      .get("http://localhost:8000/todo")
      .then((res) => {
          // console.log(res.data);
          setData(res.data);
      })
      .catch((err) => {
          console.log(err.message);
      });
}

  function handleChange(e) {
      setItem((data) => ({ ...data, title: e.target.value} ));
  }

  function handleSubmit(e) {
      e.preventDefault();

      axios
          .post("http://localhost:8000/post/todo", item)
          .then((res) => {
               setItem({ title: "", description: "" });
              console.log(res.data.message);
          })
          .catch((err) => {
              console.log("Error couldn't create TODO");
              console.log(err.message);
          });
  }



  return (
    <div class="container h-1000">
  <div class="d-flex justify-content-center align-items-center vh-80">
  <div style={{textAlign: "center"}}>
    <section>

          <form
                  onSubmit={handleSubmit}
                  className="form-container"
                  noValidate
              >
                <h1 >Add Task  <input
                      type="text"
                      name="title"
                      id="text"
                      value={data.title}
                      onChange={handleChange}


                  />
                </h1>
                  <input  size="60" type="submit" class="green" className="same btn btn-block" value="Create Todo"/>


              </form>

    </section>





              <ScrollArea
            speed={0.8}
            className="area"
            contentClassName="content"
            horizontal={false}
            >
              {Object.keys(data).map((item, i) => {

            return  <li key={item}>{data[item].title} 
            <FontAwesomeIcon icon="fa-solid fa-xmark" /></li>

            })
          } 
          </ScrollArea>









    </div>      
</div>
</div>

  );
}

export default App;
