import { useEffect, useState } from 'react';
import { BrowserRouter, Route, Routes } from 'react-router-dom';

import axios from 'axios';
import './App.css';


function App() {

  const [data, setData] = useState({ title: "", description: "" });
  const [item, setItem] = useState({ title: "", description: "" });

  useEffect(() => {
    run()
  })

function run() {
  axios
      .get("http://localhost:8000/todo")
      .then((res) => {
          // console.log(res.data);
          setData(res.data);
      })
      .catch((err) => {
          console.log(err.message);
      });
}

  function handleChange(e) {
      setItem((data) => ({ ...data, title: e.target.value} ));
  }

  function handleSubmit(e) {
      e.preventDefault();

      axios
          .post("http://localhost:8000/post/todo", item)
          .then((res) => {
               setItem({ title: "", description: "" });
              console.log(res.data.message);
          })
          .catch((err) => {
              console.log("Error couldn't create TODO");
              console.log(err.message);
          });
  }

  return (

    <div class="modal" tabindex="-1" role="dialog">
  <div class="modal-dialog" role="document">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title">Add Task</h5>
        <button type="button" class="close" data-dismiss="modal" aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div class="modal-body">
      <form
                  onSubmit={handleSubmit}
                  className="form-container"
                  noValidate
              >
                  <input
                      type="text"
                      name="title"
                      id="text"
                      value={data.title}
                      onChange={handleChange}
                      className=" same"

                  />

                  <input  size="60" type="submit" class="green" className="same btn btn-block" value="Create Todo"/>


              </form>

              <ul>
              {Object.keys(data).map((item, i) => {
              return <li>{data[item].title}</li>;
            })
          }
           </ul>
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-primary">Save changes</button>
        <button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
      </div>
    </div>
  </div>
</div>















  );
}

export default App;
