import { useEffect, useState } from 'react';
import { BrowserRouter, Route, Routes } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';
import './App.css';


function App() {

  const [data, setData] = useState({ title: "", description: "" });
  const [item, setItem] = useState({ title: "", description: "" });

  useEffect(() => {
    run()
  })

function run() {
  axios
      .get("http://localhost:8000/todo")
      .then((res) => {
          // console.log(res.data);
          setData(res.data);
      })
      .catch((err) => {
          console.log(err.message);
      });
}

  function handleChange(e) {
      setItem((data) => ({ ...data, title: e.target.value} ));
  }

  function handleSubmit(e) {
      e.preventDefault();

      axios
          .post("http://localhost:8000/post/todo", item)
          .then((res) => {
               setItem({ title: "", description: "" });
              console.log(res.data.message);
          })
          .catch((err) => {
              console.log("Error couldn't create TODO");
              console.log(err.message);
          });
  }

  return (
    <div class="container h-1000">
  <div class="d-flex justify-content-center align-items-center vh-80">
  <div style={{textAlign: "center"}}>
    <section>
       <h1 >Add Task</h1>
          <form
                  onSubmit={handleSubmit}
                  className="form-container"
                  noValidate
              >
                  <input
                      type="text"
                      name="title"
                      id="text"
                      value={data.title}
                      onChange={handleChange}


                  />

                  <input  size="60" type="submit" class="green" className="same btn btn-block" value="Create Todo"/>


              </form>

    </section>

        <section>
           <ul>
              {Object.keys(data).map((item, i) => {
              return <li>{data[item].title}</li>;
            })
          }
           </ul>
        </section>







    </div>      
</div>
</div>

  );
}

export default App;
