import { useEffect, useState } from 'react';
import { BrowserRouter, Route, Routes } from 'react-router-dom';

import axios from 'axios';
import './App.css';


function App() {

  const [data, setData] = useState({ title: "", description: "" });



 useEffect(() => {
   updateTodos()
 });

  function updateTodos() {
    axios
        .get("http://localhost:8000/todo")
        .then((res) => {
            // console.log(res.data);
            setData(res.data);
        })
        .catch((err) => {
            console.log(err.message);
        });
  }

  function handleChange(e) {
    setData((data) => ({ ...data, [e.target.name]: e.target.value }));
  }

  const handleSubmit = (e) => {
       e.preventDefault();

           axios
          .post("http://localhost:8000/post/todo", data)
          .then((res) => {

              console.log(res.data);
          })
          .catch((err) => {
              console.log("Error couldn't create TODO");
              console.log(err.message);
          });

          // console.log(data)
  }
  return (
    <div className="App">
      <header>
        <h1 className="center">Todo Tracker</h1>
      </header>
      <body>
      <form  class="add-form" onSubmit={(e) => handleSubmit(e)} >
        <div class="form-control">
          <label for="text">Task:</label>
          <input
            type="text"
            name="title"
            value={data.title}
            setValue={data.title}
            onChange={handleChange}
            id="text"
            placeholder="Add Task"
          />
          {/* <input
            type="text"
            name="description"
            value={data.description}
            onChange={handleChange}
            id="text"
            placeholder="Add Task"
          /> */}
        </div>
        <input type="submit" class="green" value="submit" class="btn btn-block" />
      </form> 
      <section className="container">
           <ul>
              {Object.keys(data).map((item, i) => {
              return<li>{data[item].titles}</li>
            })
          }


           </ul>
          <h1>{data.title}</h1>
          <h1>{data.description}</h1>
        </section>

      </body>
    </div>
  );
}

export default App;
