import { useEffect, useState } from 'react';
import { BrowserRouter, Route, Routes } from 'react-router-dom';

import axios from 'axios';
import './App.css';


function App() {

  const [data, setData] = useState({ title: "", description: "" });
  const [item, setItem] = useState({ title: "", description: "" });

  useEffect(() => {
    run()
  })

function run() {
  axios
      .get("http://localhost:8000/todo")
      .then((res) => {
          // console.log(res.data);
          setData(res.data);
      })
      .catch((err) => {
          console.log(err.message);
      });
}

  function handleChange(e) {
      setItem((data) => ({ ...data, title: e.target.value} ));
  }

  function handleSubmit(e) {
      e.preventDefault();

      axios
          .post("http://localhost:8000/post/todo", item)
          .then((res) => {
               setItem({ title: "", description: "" });
              console.log(res.data.message);
          })
          .catch((err) => {
              console.log("Error couldn't create TODO");
              console.log(err.message);
          });
  }

  return (
    <body>
      <section className="container">
          {/* <Link to="/">
              <button type="button" className="button button-back">
                  back
              </button>
          </Link> */}

          <section className="contents">
              <form
                  onSubmit={handleSubmit}
                  className="form-container"
                  noValidate
              >
                  <label className="label" htmlFor="title">
                      Title
                  </label>
                  <input
                      type="text"
                      name="title"
                      id="text"
                      value={data.title}
                      onChange={handleChange}
                      className="input"
                  />
                  <label className="label" htmlFor="description">
                      Description
                  </label>
                  <input
                      type="text"
                      name="description"
                      id="text"
                      value={data.description}
                      onChange={handleChange}
                      className="input"
                  />
                  <button type="submit"  class="green" className="btn btn-block">
                      create todo
                  </button>
              </form>
              {item.title}
          </section>
      </section>

      <section className="container">
           <ul>
              {Object.keys(data).map((item, i) => {
              return <li>{data[item].title}</li>;
            })
          }
           </ul>
        </section>

  </body>

  );
}

export default App;
